          templates,
          group_name,
          routing_details = {},
        } = viewDocGrTplRes;
        let templateIDs;

        try {
          templateIDs = templates.map(template => template.id);
        } catch (err) {
          callback(err);
          return;
        }

        createDocumentGroup({
          ids: templateIDs,
          group_name,
          token,
        }, (createErr, createRes) => {
          if (createErr) {
            callback(createErr);
            return;
          } else {
            const { id: documentGroupId } = createRes;

            viewDocumentGroup({
              id: documentGroupId,
              token,
            }, (viewDocGrErr, viewDocGrRes) => {
              if (viewDocGrErr) {
                callback(viewDocGrErr);
                return;
              } else {
                const { documents } = viewDocGrRes;
                let documentToOriginMap;
                let inviteSettings;

                try {
                  documentToOriginMap = documents.reduce((map, doc) => {
                    map[doc.origin_document_id] = doc.id;
                    return map;
                  }, {});

                  inviteSettings = Object.assign({}, (routing_details.invite_steps ? routing_details : {}), data);

                  inviteSettings.invite_steps.forEach(step => {
                    step.invite_emails = step.invite_emails.map(inviteEmail => {
                      const {
                        email,
                        subject,
                        message,
                        expiration_days,
                        reminder,
                      } = inviteEmail;

                      return {
                        email,
                        subject,
                        message,
                        expiration_days,
                        reminder,
                      };
                    });

                    step.invite_actions = step.invite_actions.map(inviteAction => {
                      const {
                        email,
                        role_name,
                        action,
                        document_id,
                        allow_reassign,
                        decline_by_signature,
                        authentication,
                      } = inviteAction;

                      return {
                        document_id: documentToOriginMap[document_id],
                        email,
                        role_name,
                        action,
                        allow_reassign,
                        decline_by_signature,
                        authentication,
                      };
                    });
                  });
                } catch (err) {
                  callback(err);
                  return;
                }

                inviteDocumentGroup({
                  id: documentGroupId,
                  data: inviteSettings,
                  token,
                }, (inviteErr, inviteRes) => {
                  if (inviteErr) {
                    callback(inviteErr);
                    return;
                  } else {
                    callback(null, inviteRes);
                    return;
                  }
                });
              }
            });
          }
        });
      }
    });
  }

}

module.exports = DocumentGroupTemplate;

